mapper.select:
select
distinct
id , username,password , name, nickname, phone, memo,STATUS,create_at, update_at,create_by,update_by,create_name ,update_name , reason,disable_at FROM user u LEFT JOIN (SELECT id AS ids , username AS create_name FROM user) us ON create_by = ids LEFT JOIN (SELECT id AS idss , username AS update_name FROM user) uss ON update_by = idss
order by ${orderByClause}
limit ${pageStart},${pageSize}

SELECT

FROM  user
WHERE id = #{userId,jdbcType=INTEGER}
ORDER BY id DESC LIMIT 1;

SELECT

FROM  user
WHERE username = #{userAccount,jdbcType=VARCHAR}
ORDER BY id DESC LIMIT 1;

